mapper.select:
SELECT *
FROM BOOK
WHERE ID = #{id, jdbcType=VARCHAR}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER () AS TOTCNT FROM ( SELECT B.ID, R.NAME ROOM, U.NAME, B.COUNT, TO_CHAR(B.CHECK_IN, 'YYMMDD') AS CHECK_IN, TO_CHAR(B.CHECK_OUT, 'YYMMDD') AS CHECK_OUT, B.PLEASE FROM HOTEL H, BOOK B, ROOM R, USERS U WHERE B.ROOM_ID = R.ID AND B.USER_ID = U.ID AND H.USER_ID = #{userId, jdbcType=VARCHAR} AND B.STATUS = #{status}
AND B.REMOVE_DATE IS NOT NULL AND B.REMOVE_DATE IS NULL
GROUP BY B.ID, R.NAME, U.NAME, B.COUNT, B.CHECK_IN, B.CHECK_OUT, B.PLEASE ORDER BY B.CHECK_IN)A) WHERE RNUM >= #{viewPage} AND RNUM <= #{limit}

mapper.update:
UPDATE BOOK
SET REMOVE_DATE = SYSDATE, STATUS = 'CANCEL'
WHERE ID = #{id, jdbcType=VARCHAR}

mapper.select:
SELECT B.ID
FROM BOOK B, ROOM R
WHERE B.USER_ID = #{userId}
  AND R.HOTEL_ID = #{hotelId}
  AND B.REVIEW_ID IS NULL
GROUP BY B.ID
