mapper.update:
update hotel
set name = #{name}, service = #{service}, zonecode = #{zonecode}, sido = #{sido}, sigungu = #{sigungu}, address = #{address}, detail = #{detail}
where id = #{id}

mapper.select:
SELECT H.ID, SUM(S.CLEAN) CLEAN, SUM(S.FACILITIES) FACILITIES, SUM(S.CONDITION) CONDITION, SUM(S.ORGANIC) ORGANIC, COUNT(S.CLEAN) TOTCNT FROM HOTEL H, STAR S WHERE H.ID IN
#{hotel.id, jdbcType = VARCHAR}
AND H.ID = S.HOTEL_ID(+) GROUP BY H.ID ORDER BY H.ID

SELECT H.ID, MIN(R.NORMAL_PRICE) PRICE FROM HOTEL H, ROOM R WHERE H.ID = R.HOTEL_ID AND H.ID IN
#{hotel.id, jdbcType = VARCHAR}
GROUP BY H.ID, R.NORMAL_PRICE, R.ID, R.HOTEL_ID ORDER BY H.ID, R.NORMAL_PRICE
